import React, { useState, useEffect } from 'react';
import axios from 'axios';

const App = () => {
  const [inputText, setInputText] = useState('');
  const [summary, setSummary] = useState('');
  const [audio, setAudio] = useState(null);  // State to track the audio object
  const [isPlaying, setIsPlaying] = useState(false);  // State to track if audio is playing

  const handleSummarize = async () => {
    try {
      const response = await axios.post('http://localhost:5555/summarize', {
        text: inputText,
      });
      setSummary(response.data.summary);
    } catch (error) {
      console.error("Error summarizing:", error);
    }
  };

  const handleReadAloud = async () => {
    if (isPlaying && audio) {
      // If audio is playing, pause it
      audio.pause();
      audio.currentTime = 0;  // Reset the audio to the start
      setIsPlaying(false);
      return;
    }

    try {
      const response = await axios.post('http://localhost:5555/read-aloud', {
        text: summary,
      }, { responseType: 'blob' });  // Get audio as a blob

      const audioUrl = URL.createObjectURL(response.data);
      const newAudio = new Audio(audioUrl);

      // Play the audio
      newAudio.play();
      setAudio(newAudio);  // Store the new audio object
      setIsPlaying(true);  // Set playing state to true

      // When audio finishes playing, reset the playing state
      newAudio.onended = () => {
        setIsPlaying(false);
      };

    } catch (error) {
      console.error("Error with TTS:", error);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>SummarizeMe</h1>
      <textarea
        value={inputText}
        onChange={(e) => setInputText(e.target.value)}
        placeholder="Enter text or URL"
        rows="6"
        cols="60"
      />
      <br />
      <button onClick={handleSummarize}>Summarize</button>
      <div>
        <h3>Summary:</h3>
        <p>{summary}</p>
        <button onClick={handleReadAloud}>
          {isPlaying ? 'Stop' : 'Read Aloud'}  {/* Toggle button text */}
        </button>
      </div>
    </div>
  );
};

export default App;
